const getDefaultPath = (template, options) => {
    let generatedPath = './'
    switch (template) {
        case 'Component':
        case "Form":
            generatedPath = options && options.isShared ?
                "./src/shared/components" :
                "./src/views/"
            break
        case 'Service':
            generatedPath = "./src/services/"
        case 'Model':
            generatedPath = "./src/model/"
            case 'Validator':
            case 'Form':
            generatedPath = "./src/views/"
        default:
            break;
    }
    return generatedPath
}

module.exports = {
    getDefaultPath
}