type KEY = "upload" | "input" | "select" | "otp" | "date" | "switch" | "radio";
const FormComponents = (component: KEY, name: string) => {
  function renderComponent(component: KEY) {
    switch (component) {
      case "input": {
        return `
            <InputField
            placeholder="Enter"
            name="${name}"
        />`;
      }
      case "select": {
        return `
        <DropdownField
            options={[]}
            name="${name}"
            placeholder="Select"
            onChange={(value) => setFieldValue("${name}", value)}
            value={values?.${name}}
        />`;
      }
      case "switch": {
        return `
        <SwitchComponent
            value={values?.${name}}
            onSwitch={(value) => setFieldValue("${name}", value)}
        />`;
      }
      case "date": {
        return `
        <DatePickerField
            name="${name}"
            placeholder="Enter"
            value={values?.${name}}
            onChange={(date, dateString) => {
            setFieldValue("${name}", dateString);
            }}
        />`;
      }
      case "radio": {
        return `<RadioComponent
            name="${name}"
            value={values?.${name}}
            data={[]}
            onChange={(value) => setFieldValue("${name}", value)}
        />`;
      }
      case "otp": {
        return `
            <OTPField
              count={4}
              onChange={(otp) => {
                setFieldValue("${name}", otp);
              }}
            />
        `;
      }
      default: {
        return "";
      }
    }
  }
  return renderComponent(component);
};
export default FormComponents;
